import React, { Component } from 'react';
import MovieList from '../components/MovieList'

class Searchbox extends Component {
 constructor(){
    super();

    this.state={
      search:'',
    };
  }

  searchMovie=(event)=>{
    let movie = event.target.value;
    this.setState({search:movie})
  }  	

  handleLoading = () => {
    if(this.props.loading) {
      return <div>Loading Students...</div>
    } else {

      return <MovieList studentData={this.props.studentData} search={this.state.search}/>
    }
  }

  render(){
  	return(
  		<div>
  			<input type="text" id='name-input' className="input" placeholder="Search by name"  onChange={(e)=>this.searchMovie(e)} />
  			{this.handleLoading()}
  		</div>
  	)
  }

}

export default Searchbox;
