import adapter from './adapter'


const data = {

  listStreams: () => {
    listOfStreams
    .then (json => pushStreams())
  },

  pushStreams: () => { 
    listStreams.forEach(stream=> {
      streams.push(stream)
    })
  },

  getStreams: () => {
    return streams
  }
}


export default data;
