import React from 'react';
import adapter from './adapter'

let listOfStreams = adapter.getStreams()
.then(streamsList => streamsList)

console.log(listOfStreams)

async function apiGetAll () {
   try {
     const resp = await listOfStreams 
     console.log(resp)
     return resp
   } catch (err) {
        console.log(err)
     }
}

apiGetAll()

class Data extends React.Component {
  constructor() {
    super()
    this.state = {
      streams: [{"name": "fig"}, {"name": "Donna"}]
    }
  }


componentDidMount () {
   	listOfStreams
    .then (json => 
          this.setState({
          streams: json
     }))
  }  

  render() { 
    const resArr = this.state.streams.map((r, i)=> {
      return (<li key={i}>
        {r.name}
      </li>
      )
    })  
    return (
      <div>
        <h1>Welcome to my App!</h1>
        <ul>
        {resArr}
        </ul>
    </div>
    )
  }
}

export default Data;
