const URL = "http://localhost:3000/"
const adapter = {
  getStreams: () => {
    return fetch(`${URL}/streams`)
    .then(resp => resp.json())
  },

  getMovies: (stream_id) => {
    return fetch(`${URL}streams/${stream_id}/movies`,{
      headers: { "Content-Type": "application/json" },
    })
    .then(resp => resp.json())
  },
}

export default adapter;
