import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchTrailers } from '../actions/trailerActions'
import MovieInfo from '../components/MovieInfo';

class InfoPage extends Component {  
  componentDidMount() {
  console.log(this); 
  this.props.fetchTrailers(this.props.currentMovie.Title)	
  }


  handleLoading = () => {
    if(this.props.loading) {
      return <div>Loading Movies...</div>
    } else {
      return (
      <div>
      <MovieInfo currentMovie={this.props.currentMovie} trailer={this.props.currentMovie.Title}/>
      </div>
      )
    }
  }

  render() {
    return (
    <div className= "MovieInfo">
    	{this.handleLoading()}
	</div>
 	);
  }
}

const mapDispatchToProps = state => {
  return {
    trailer: state.TrailersReducer.trailers,
    loading: state.TrailersReducer.loading
  }
}

export default connect(mapDispatchToProps, {fetchTrailers})(InfoPage)
