import React, { Component } from 'react';

class Searchbox extends Component {
 constructor(){
    super();

    this.state={
      search:'',
    };
  }

  searchMovie=(event)=>{
    let movie = event.target.value;
    this.setState({search:movie})
  }  	


  render(){
  	return(
  		<div>
  			<input type="text" id='name-input' className="input" placeholder="Search by name"  onChange={(e)=>this.searchMovie(e)} />
  			{this.handleLoading()}
  		</div>
  	)
  }

}

export default Searchbox;
