    super(props)

    this.state={
      result:'',
    };    
  }

  componentDidMount() {
    this.showMovies(text)
  }

  componentDidUpdate() {
    this.showMovies(text)
  }


  showMovies= (text) => {
    this.setState({
      result: text
    })          
  }

  render() {   
      if (this.props.movie.slug.includes(this.props.search)){
        text = this.props.movie.slug
      }
      else{
          text = ''
      }
      return (
        <div className="moiveCard">
          <h1> {this.props.movie.slug} </h1>
