import adapter from '../adapter'
let streams

function listOfStreams() {
  streams = adapter.getStreams().then(streamsList => streamsList)
  console.log(streams)
  return streams
}

function streamsReducer(state = [], action) {

  listOfStreams().then (json => state=({streamsList: json}))
  console.log(state)
  return state;

}

export default streamsReducer
