import adapter from './adapter'


let listOfStreams = adapter.getStreams()
.then(streamsList => streamsList)

let streams = [];

const data = {

  pushStreams: () => { 
    data.listStreams.forEach(stream=> {
     streams.push(stream)
    })
  },


  listStreams: () => {
    data.listOfStreams
    .then (json => data.pushStreams())
  },

  getStreams: () => {
    return streams
    console.log(streams)
  }
}


export default data;
