import React, { Component } from 'react';
let currentMovie = this.props.currentMovie
class MovieInfo extends Component {
  render() {
  	function ratings() {this.props.currentMovie.Ratings.forEach(rating =>{
  		return<h5>{rating.Source}: {rating.Value}</h5>
  	})
  }
  	console.log(ratings)
    return (
    	 <div className = "show_Movie" >
        <img className= "selected_Movie" alt={this.props.currentMovie.Title} src={this.props.currentMovie.Poster}></img>
          <h1> {this.props.currentMovie.Title} </h1>
          <h4> Rated: {this.props.currentMovie.Rated} </h4> 
          <h4> Genre: {this.props.currentMovie.Genre} </h4>
          <h5> Actors: {this.props.currentMovie.Actors} </h5>
          <h5> Director: {this.props.currentMovie.Director} </h5>
          <h5> Awards: {this.props.currentMovie.Awards} </h5>
		  <h4> Ratings</h4>
		  {ratings()}
          <h4> Plot</h4>
          <h5>{this.props.currentMovie.Plot} </h5>


        </div>
    )
  }
}
export default MovieInfo;
