import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchMovies } from '../actions/movieActions'
import MovieList from '../components/MovieList'

class MoviesPage extends Component {   

  componentDidMount() {
  console.log(this.props)
  this.props.fetchMovies(this.props.handler)
  }

  trailerID = (trailer) => { 
    console.log(trailer);
    //link trailer to backend
  }


  handleLoading = () => {
    if(this.props.loading) {
      return <div>Loading Movies...</div>
    } else {
      return (
      <div>
      <MovieList movieCards={this.props.movieCards} trailerID={this.trailerID} currentStream={this.currentStream}/>
      </div>
      )
    }
  }

  render() {
    return (
      <div className="App">
        <div className="banner-2">{this.props.streamName.toUpperCase()}</div>
        {this.handleLoading()}
      </div>
    );
  }
}



const mapDispatchToProps = state => {
  return {
    movieCards: state.MoviesReducer.movies,
    loading: state.MoviesReducer.loading
  }
}

export default connect(mapDispatchToProps, {fetchMovies})(MoviesPage)
