import React from 'react';
import adapter from './adapter'


var streamsL = []
let listOfStreams = adapter.getStreams()
.then(streamsList => streamsList)


class Data extends React.Component {
  constructor() {
    super()
    this.state = {
      streamsList: []
    }
  }


componentDidMount () {
   	listOfStreams
    .then (json => 
          this.setState({
          streamsList: json
     }))
  }  


  streams() {
     streamsL = this.state.streamsList
     return streamsL
   } 


  render() { 
    const resArr = this.state.streamsList.map((r, i)=> {
      streamsL.push(r.name)
      return (<p key={i}>
        {r.name}
      </p>
      )
    })  
    return (
      <div>
        {resArr}
    </div>
    )
  }
}


export default Data;
