const PROXY = "https://cors-anywhere.herokuapp.com/";
const streamsAPI = 'http://river-backend.herokuapp.com/streams'
const URL = PROXY + streamsAPI
const movieList = []

function showMe(movie){
	fetch(`http://www.omdbapi.com/?t=${movie}+&apikey=6b46131b`).then(response => {
	 	return response.json()
    }).then(responseJSON => {
    	movieList.add(responseJSON)
    });
}

export const fetchMovies = () => {
	return (dispatch) => {
		dispatch({ type: 'LOADING_MOVIES'})
		fetch(`${URL}/1/movies`).then(response => {
      		return response.json()
    	}).then(responseJSON => {
    		responseJSON.forEach(movie => {		
				showMe(movie)
			});
			console.log(movieList)
			console.log(responseJSON)
      		dispatch({ type: 'ADD_MOVIES', movies: responseJSON})
    	})
	}
}
