import React, { Component } from 'react';
import { connect } from 'react-redux';
import { listMovies } from '../actions/movieActions'
import Movie from './Movie';

class Searchbox extends Component {
 constructor(){
    super();

    this.state={
      search:'',
    };
  }

  componentDidMount() {
    this.props.listMovies()
    console.log(this)
  }

  searchMovie=(event)=>{
    let keyword = event.target.value;
    this.setState({search:keyword})
    console.log(this.state.search)
  }  	

  handleLoading = () => {
    if(this.props.loading) {
      return <div>Loading Movies...</div>
    } 
    else {

    let movieList = ''
       movieList = this.props.movieCards.filter((movie)=>{
      if(this.state.search === ''){

            return movie

      }
      else if(movie.title.toLowerCase().includes(this.state.search.toLowerCase())){
            return movie
      }        
      }).map((movie,index)=>{
        let  moviesList = this.props.movieCards.map((movie, index) => {
        return <Movie key={index} movie={movie} movieID={(index+1)}/>
      })

      return (
        <div className="movie-list">
            {moviesList}
        </div>
      )
      })
    }
  }

  render(){
  	return(
  		<div>
  			<input type="text" id='name-input' className="input" placeholder="Search by title"  onChange={(e)=>this.searchMovie(e)} />
  			{this.handleLoading()}
  		</div>
  	)
  }

}

const mapDispatchToProps = state => {
  return {
    movieCards: state.MoviesReducer.movies,
    loading: state.MoviesReducer.loading
  }
}

export default connect(mapDispatchToProps, {listMovies})(Searchbox)
