const streamsAPI = 'http://localhost:3000/streams'

export const fetchStreams = (streamsAPI) => {
	return (dispatch) => {
		dispatch({ type: 'LOADING_STREAMS'})
		fetch('streamsAPI').then(response => {
      		return response.json()
    	}).then(responseJSON => {
      		dispatch({ type: 'ADD_STREAMS', streams: responseJSON})
    	})
	}
}
