import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchUserStreams } from '../actions/userStreamActions'
import Profile from '../components/Profile';
import { Auth0Context } from "../react-auth0-spa";
import { Fragment } from "react";


class ProfilePage extends Component {
 static contextType = Auth0Context;  

  constructor(props){
    super(props)
    this.handler = this.handler.bind(this)
  }

  componentDidMount() {
  }

  componentDidUpdate(prevProps){
  }

  fetchUserStreams = () => {
  	this.props.fetchUserStreams()
  }

  handler = (id) => {
    this.setState({
      currentUserId: id,
    })
  }



  handleLoading = () => {
    if(this.props.loading) 
    {
      return <div>Loading Profile...</div>
    } 

  }

  render() {
    const { loading, user } = this.context;
    const { isAuthenticated, loginWithRedirect} = this.context;
    if (loading || !user) {
      return <div>Loading...</div>;
    }

    return (
    	<div className= "Profile">
      //profile component goes here
        <Fragment>
        <h2>Hi, {user.email}</h2>
        {console.log(user)}
        </Fragment>
      <div>
         {isAuthenticated && (
            adapter.createUser(user.email,user.name).then(res=> {
              user.id = res.id
            }),            
            <div className="greeting">
              <h2> Hi {user.name} </h2>
            </div>
        )}     
      </div>        
		</div>			
 	);
  }
}

const adapter = {
  createUser: (email,username) => {
    return fetch(`https://cors-anywhere.herokuapp.com/https://river-api.herokuapp.com/users`, {
      method: 'POST',
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({email, username})
    })
  .then(res => res.json())       
  }
}

const mapDispatchToProps = state => {
  return {
    userStreams: state.StreamsReducer.streams,
    loading: state.StreamsReducer.loading
  }
}

export default connect(mapDispatchToProps, {fetchUserStreams})(ProfilePage)
