import React from 'react';
import '../index.css';
import Movie from './Movie';
import InfoPage from '../containers/InfoPage'
import {withRouter} from 'react-router-dom';

let showMovie
let moviesList


class MovieList extends React.Component {
  componentDidMount() {

   }

//binds passed handler to StreamsList handler
  constructor(props){
    super(props)
    this.handler = this.handler.bind(this);
    this.state = {clicked: false}

  }

// recieves id from passed handler
  handler = (movie, clicked) => {
    this.setState({
      currentMovie: movie,
      clicked: true
    })   
  }

  trailerID = (trailer) => { 
    this.props.trailerID(trailer)
  }

  render() {
    if (this.state.clicked !== true) {   
       moviesList = this.props.movieCards.map((movie, index) => {
        return <Movie key={index} movie={movie} handler={this.handler}/>
      })

      return (
        <div className="movie-list">
            {moviesList}
        </div>
      )
    }
    else
      showMovie = this.state.currentMovie

      return(
      <div>
        <br/>
        <InfoPage streamID={this.props.streamID} currentMovie={showMovie} trailerID={this.trailerID}/>
        <div className="movie-list">
            {moviesList}
        </div>
      </div>
    )
  }
}

export default withRouter(MovieList);
