import React, { Component } from 'react';
import Video from './Video';

class MovieInfo extends Component {
  constructor(props){
    super(props)
    this.state = {
      currentMovie: this.props.currentMovie,
      videoId: ' ',
      fullSrc: ' ' 
    }

  }   

  componentDidMount() {
    this.props.path(this.props.selectedMovie)
  }

   componentDidUpdate(prevProps){

    if (this.props.currentMovie.Title !== prevProps.currentMovie.Title && this.props.selectedMovie === "")
    {
      console.log('different title')
      //this.props.path(this.props.selectedMovie)
    } 
  }

  shouldTrailerLoad = () => {
    if (this.props.selectedMovie.youtube_id !== null) {
      this.setState ({
        fullSrc: 'https://www.youtube.com/embed/' + this.props.selectedMovie.youtube_id,
        videoId: this.props.selectedMovie.youtube_id
      })

      console.log('youtube id is present on backend')
    }
    else{
      this.setState ({
        fullSrc: 'https://www.youtube.com/embed/' + this.props.trailer,
        videoId: this.props.trailer
      })

      //trigged incoreectly
      console.log('trailer fetched from youtube')
    }
  }

  render() {

    this.shouldTrailerLoad()
    //console.log(this.props.selectedMovie )
    //error when selecting from search

    let errorMsg

    if (this.props.trailer === "kJQP7kiw5Fk"){
      errorMsg = "due to an issue with this sites connection to youtube the trailer can not be viewed at this time";
      this.props.handler("default")
      this.setState ({videoId: this.props.trailer})
    }
    else{
      this.props.handler(this.props.trailer)
    }

  	const ratings = this.state.currentMovie.Ratings.map((rating, index) =>(
  		<li  key={index}>» {rating.Source}: {rating.Value} «</li>  
 	  ));    

    return (
    	<div className = "show_Movie" >
        <img className= "selected_Movie" alt={this.state.currentMovie.Title} src={this.state.currentMovie.Poster}></img>
          <h4> Rated: {this.state.currentMovie.Rated}</h4>   
          <h4> Year: {this.state.currentMovie.Year}</h4> 
          <h4> Genre: {this.state.currentMovie.Genre} </h4>
          <h5> Actors: {this.state.currentMovie.Actors} </h5>
          <h5> Director: {this.state.currentMovie.Director} </h5>
          <h5> Awards: {this.state.currentMovie.Awards} </h5>
		    <h4> Ratings </h4>
        <ul style={{display: 'inline',margin: 40, listStyle: 'none'}}> {ratings} </ul>
          <h4> Plot</h4>
          <h5>{this.state.currentMovie.Plot} </h5>
        <Video fullSrc= {this.state.fullSrc} videoID = {this.state.videoId}/>
        <p>{errorMsg}</p>        
      </div>
    )
  }
}
export default MovieInfo;
