import { listMovies } from '../actions/movieActions'

class Searchbox extends Component {
 constructor(){
    super();

    this.state={
      search:'',
    };
  }

  componentDidMount() {
    this.props.listMovies()
    console.log(this)
  }

  searchMovie=(event)=>{
    let movie = event.target.value;
    this.setState({search:movie})
  }  	

  handleLoading = () => {
    if(this.props.loading) {
      return <div>Loading Movies...</div>
    } else {

    let movieList = ''
    if (this.state.StudentData !== ""){
       movieList = this.state.StudentData.filter((student)=>{
      if(this.props.search === ''){

            return student

      }
      else if(student.firstName.toLowerCase().includes(this.props.search.toLowerCase()) || student.lastName.toLowerCase().includes(this.props.search.toLowerCase())){
            return student
      }        
      }).map(student=>{
          let total = 0
          this.state.dropDownOpen = student.grades
          for(let i = 0; i < student.grades.length; i++){total = (total + +student.grades[i])}
          let avg = total/student.grades.length
        return(
          <div className = "card">
              <img className='profilePic' alt={'profile pic'} src={student.pic}></img>
              <li className = "list">
              <h1 className= "name">
              {student.firstName} {student.lastName}
              </h1>
              <p className ="dropDown" onclick= {this.handleClick}>{defaultDrop}</p>
              <p>Email: {student.email}</p>
              <p>Company: {student.company}</p>
              <p>Skill: {student.skill}</p>
              <p>Avearge: {avg}%</p>
              <p>{this.state.dropDownOpen}</p>
              </li>
          </div>

        )
      })

    }
    else{
      movieList = ""
    }     
    }
  }

  render(){
  	return(
  		<div>
  			<input type="text" id='name-input' className="input" placeholder="Search by name"  onChange={(e)=>this.searchMovie(e)} />
  			{this.handleLoading()}
  		</div>
  	)
  }

}

const mapDispatchToProps = state => {
  return {
    movieCards: state.MoviesReducer.movies,
    loading: state.MoviesReducer.loading
  }
}

export default connect(mapDispatchToProps, {listMovies})(Searchbox)
