          <h5> Awards: {this.props.currentMovie.Awards} </h5>
		  <h4> Ratings</h4>

          <h4> Plot</h4>
          <h5>{this.props.currentMovie.Plot} </h5>


        </div>
    )
  }
}
export default MovieInfo;
