import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchMovies } from '../actions/movieActions'
import MovieList from '../components/MovieList'

class MoviesPage extends Component {   

  componentDidMount() {
    console.log(this)
    this.props.fetchMovies(1)
  }
//binds passed handler to StreamsPage handler
  constructor(props){
    super(props)

    this.handler = this.handler.bind(this)
  }
// recieves id from passed handler
  handler = id => {
    this.setState({
      currentStream: id
    })
 //returns the selected Stream id to App       
    this.props.handler(id)
  }

  handleLoading = () => {
    if(this.props.loading) {
      return <div>Loading Movies...</div>
    } else {
      return (
      <div>
      <MovieList movieCards={this.props.movieCards}/>
      </div>
      )
    }
  }

  render() {
    return (
      <div className="App">
        {this.handleLoading()}
      </div>
    );
  }
}



const mapDispatchToProps = state => {
  return {
    movieCards: state.MoviesReducer.movies,
    currentStream : state.StreamsReducer.currentStream,
    loading: state.MoviesReducer.loading
  }
}

export default connect(mapDispatchToProps, {fetchMovies})(MoviesPage)
