import adapter from '../adapter'
let streams


function streamsReducer(state = [], action) {
  adapter.getStreams().then(streamsList => streamsList)
  .then (json => this.setState({
      stream: json
    }));

  console.log(state)
  return [...state];

}

export default streamsReducer
