const currentStreams = {
	streams: [],
};
var streamsList = [
{name: "netflix"},
{name: "hulu"},
{name: "amazon"},
{name: "hbo"},
{name: "disney"},
{name: "showtime"}];

class Stream{
	constructor(name){
		this.name = name;
	}

}

var getStreamsList = new Promise((resolve, reject) => {
const xhr = new XMLHttpRequest();
xhr.open("http://localhost:3000/streams");
xhr.onload = () => resolve(JSON.parse(xhr.responseText));
xhr.onerror = () => reject(xhr.statusText);
xhr.send();
});


var getStreamDetails = getStreamsList
.then(function (fulfilledList) {
        console.log(fulfilledList.results);
        var streamList = [];
        setTimeout(() => {
            fulfilledList.results.forEach(function (strem) {
                const xhr = new XMLHttpRequest();
                xhr.open("GET", strem.url);
                xhr.onreadystatechange = function () {
                    if (xhr.readyState === 4) {
                        streamList.push(JSON.parse(xhr.responseText));
                    }
                };
                xhr.send();
            });
        }, 3000); 
        return streamList;
          })
    .then(function (fulfilledListDetails) {
        console.log(fulfilledListDetails);
    })
    .catch(function (error) {
        console.log(error.message);
    });




export default streamsList
