const PROXY = "https://cors-anywhere.herokuapp.com/";
const streamsAPI = 'http://river-backend.herokuapp.com/streams'
const URL = PROXY + streamsAPI



export const fetchMovies = () => {
	return (dispatch) => {
		dispatch({ type: 'LOADING_MOVIES'})
		fetch(`${URL}/1/movies`).then(response => {
      		return response.json()
    	}).then(responseJSON => {
    		let moviesList = []
    		for (let i = 0; i < responseJSON.length; i++){
    		fetch(`http://www.omdbapi.com/?t=${responseJSON[i]}+&apikey=6b46131b`).then(response => {
    			return response.json()
    		}).then(resp => {
    			moviesList.push(resp)
    		})
    	}
    		console.log(moviesList)
    		//let fullMoive = responseJSON[0]
    		//console.log(fullMoive)
      		dispatch({ type: 'ADD_MOVIES', movies: responseJSON})
    	})
	}
}
