import React from 'react';
import '../index.css';
import Movie from './Movie';

let showMovie
let moviesList

class MovieList extends React.Component {
  componentDidMount() {
  }

//binds passed handler to StreamsList handler
  constructor(props){
    super(props)
    this.handler = this.handler.bind(this);
    this.state = {clicked: false}
  }

// recieves id from passed handler
  handler = (movie, clicked) => {
    this.setState({
      currentMovie: movie,
      clicked: true
    })    
  }

  render() {
    if (this.state.clicked !== true) {   
       moviesList = this.props.movieCards.map((movie, index) => {
        return <Movie key={index} movie={movie} handler={this.handler}/>
      })

      return (
        <div className="movie-list">
            {moviesList}
        </div>
      )
    }
    else
      showMovie = this.state.currentMovie
      return(
        <div className = "showMovie" style={{
            backgroundImage: "url(" + showMovie.Poster + ")" ,
              backgroundPosition: 'center',
              backgroundSize: 'cover',
              backgroundRepeat: 'no-repeat'}}
            >
          <h1> {showMovie.Title}</h1>
           <div className="movie-list">
            {moviesList}
          </div>
        </div>
    )
  }
}

export default MovieList;
