import React, { Component } from 'react';
import { addStream } from '../actions';
import { connect } from 'react-redux'; 
import adapter from './adapter'

  let listOfStreams = adapter.getStreams()
  .then(streamsList => streamsList)	


  export class StreamInput extends Component {

  state = {
    streamsList: []
  }



  componentDidMount () {
   	listOfStreams
    .then (json => 
          this.setState({
          streamsList: json
     }))
  }  

  handleOnChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  }


  handleOnSubmit = event => {
    event.preventDefault();
    const book = {...this.state, id: uuid() };
    this.props.addBook(book);
    this.setState({
      title: '',
      authorName: ''
    });
  }

  render() { 
    const resArr = this.state.streamsList.map((r, i)=> {
      return (<p className= "stream" id = {r.name} key={i}>
        {r.name}
      </p>
      )
    })  
    return (
      <div>
        {resArr}
    </div>
    )
  }
}

export default connect(null, { addBook })(BookInput);
