import React, { Component } from 'react';
import logo from'../logo.svg';

class Stream extends Component {

  handleOnClick = () => {
     console.log(this.props.stream.name)
     this.setstate({stream: this.props.stream.name})
   }

  render() {
    return (
      <div>
        <img onClick={this.handleOnClick} className= "stream" id={this.props.stream.name} src={logo}>
        </img>
      </div>
    );
  }

};

export default Stream;
