var casper = require("casper").create()

var url = 'https://www.justwatch.com/us/provider/netflix/movies',
title = [];

function getTitle() {
	var title = document.querySelector('.title-poster');
	return Array.prototype.map.call(title, function(e) {
		return e. innerText;
	});
}

casper.start(url,function(){
	this.echo("Start ...");
});

casper.waitforSelector('md infinite-scroll-enabled hydrated',
function() {
	console.log('the page is loaded')
});

casper.then(function(){
	title = this.evaluate(getTitle);
})
