import amc from'../streams_logos/amc.png'
import fx from'../streams_logos/fx.png'
import syfy from'../streams_logos/syfy.png'
import ifc from'../streams_logos/ifc.png'



const Profile = () => {
  const { loading, user } = useAuth0();

  var streamsList = []
  var lists

  function stuff(){
    adapter.fetchUserStream()
    .then(userStreams => {
        var lists = displatList(userStreams)
    })
  }


  function handleClick() {

  }  

  function displatList(list){  lists = list.map((stream, index) => {
        return <img key={index} className= "stream2" alt={stream.name} src={stream.img}/>
      })
  }
  if (loading || !user) {
    return <div>Loading...</div>;
  }


  return (
  <div className = "profile">
    <Fragment>
      <img src={user.picture} alt="Profile" />
      <h2>Hi, {user.name}</h2>
      <p>email: {user.email}</p>
      <button onClick={handleClick}>Edit Streams</button>
    </Fragment>
    <div className="streams-list">
      {stuff}
    </div>
  </div>
  );
};


const adapter = {
  fetchUserStream: (user_id) => {
    return fetch(`https://cors-anywhere.herokuapp.com/https://river-api.herokuapp.com/users/${user_id}/user_streams`)
    .then(resp => resp.json())       
  }
}


export default Profile;
