import React from 'react';
import '../index.css';
import Stream from './Stream';

let streamState

class StreamList extends React.Component {

  componentDidMount() {
    streamState = this 
    console.log(this)
  }
  handleOnClick = () => {
    streamState.setState({
        currentStream: this.props.stream.id
    });
    }

  render() {    
    const streamsList = this.props.streamInfo.map((stream, index) => {
      return <Stream key={index} stream={stream} thisStream={this.props.currentStream} />
    })

    return (
      <div className="streams-list">
          {streamsList}
      </div>
    )
  }
}

export default StreamList;
