import React from 'react';
import adapter from './adapter'


let listOfStreams = adapter.getStreams()
.then(streamsList => streamsList)

const Data ={

  listStreams: () => {
    listOfStreams
    .then (json => 
          this.setState({
          streamsList: json
     }))
  },
}


export default Data;
