import React, { Component } from 'react';
import { connect } from 'react-redux';
import { listMovies } from '../actions/movieActions'
import MoviesList from './MoviesList';
import { Auth0Context } from "../react-auth0-spa";
import { findDOMNode } from 'react-dom';
import $ from 'jquery'; 

let  moviesList
let count = 0
//close sereach area if input box lose fousand input box is empty

class Searchbox extends Component {
 constructor(props){
    super(props);

    this.state={
      search:'',
      MoiveData: "",
      open: this.props.sBoxOpenState,
      searchState: '',
      setClass: '',
      count: 0
    };
  }

  getStuff=()=>{
    let theSearchState =''
    let theSetClass = ''
    if (this.props.sBoxOpenState === false){
      console.log('returned false')
      theSearchState= <p className={"searchBar"} onClick={this.handleClick}> Search </p>
      } 
    else{
      console.log('returned true')
      theSearchState= <input type="text" id="name-input" className="searchBar" placeholder="Search by title" onClick={this.props.pointer} onChange={(e)=>this.searchMovie(e)} />
      theSetClass= 'searchBackground'
      }
      console.log(theSearchState)
      console.log(theSetClass)
    return [theSearchState, theSetClass]
  }

  handleStuff(){
    let results = this.getStuff()
    this.setState({
      searchState: results[0],
      setClass: results[1]
    }) 
  }

  componentDidMount() {

    this.props.listMovies()
    this.handleStuff()
  }

  componentDidUpdate(){

    if (this.state.count < 1){
      console.log('updated')
      this.handleStuff()
      this.setState({
        count : count + 1
      })
    }
  }


  handleClick=()=>{
    console.log("clicked")
    this.setState({
      count: 0
    })
    //this.setState({open:true})
    //console.log(this.state.open)
  }

  searchMovie=(event)=>{
    let keyword = event.target.value.toLowerCase();
    this.setState({search:keyword})

  }  	

  handleLoading = () => {
    if(this.props.loading) {
      return ' '
    } 
    else {
      if (this.props.movieCards === undefined){
        window.location.reload();
      } 
      let movieList


      movieList = this.props.movieCards.filter(movieCard=>{
      if(this.state.search.length >= 3 ){
            return movieCard
      }
      else{
        moviesList = ''
      }        
      }).map((movie,index)=>{
        moviesList = this.props.movieCards.map((movie, index) => {
          return <MoviesList key={index} search={this.state.search}  movie={movie} movieID={(index+1)} cardStyle='height: -webkit-fill-available;
          overflow: hidden;'/>
        })
      })
    }
  }

  render(){
  	return(
      /* onClick={() => this.loseFocus(this.props.target)} */
  		<div className="searchBar" onClick={this.props.pointer}  onBlur= {() => this.loseFocus(this.props.target)}>
  			{this.state.searchState}
  			{this.handleLoading()}
        <div onClick={this.props.pointer} className={this.state.setClass} >
          <div className="search-list" onClick={this.props.pointer}>
              {moviesList}
          </div>
        </div>
  		</div>
  	)
  }

}

const mapDispatchToProps = state => {
  return {
    movieCards: state.MoviesReducer.movies,
    loading: state.MoviesReducer.loading
  }
}

export default connect(mapDispatchToProps, {listMovies})(Searchbox)
